sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var getStatus = g_form.getValue("status");
    var userId = g_user.userID;
    var reviewer = g_form.getValue('reviewer');
    var requestOwner = g_form.getValue('request_owner');
    var isApproveRequired = g_form.getValue('is_approval_required');
    var isRecurrence = g_form.getValue('is_recurring');
	alert(getStatus);
    g_form.setReadOnly('due_date', 'status' == 'Submitted');
    g_form.setReadOnly('is_approval_required', 'status' == 'Submitted');
    g_form.setReadOnly('is_recurring', 'status' == 'Submitted');
    g_form.setReadOnly('description', 'status' == 'Submitted');
    if (isApproveRequired) {
        g_form.setReadOnly('reviewer', 'status' == 'Submitted');
    }
    if (isRecurrence) {
        g_form.setReadOnly('recurrence_pattern', 'status' == 'Submitted');
        g_form.setReadOnly('days', 'status' == 'Submitted');
        g_form.setReadOnly('recurrence_date', 'status' == 'Submitted');
        g_form.setReadOnly('start_date', 'status' == 'Submitted');
        g_form.setReadOnly('end_date', 'status' == 'Submitted');
    }
}
